      </div>

      {/* Center: Project and Conversation Selector */}
      <div className="flex items-center space-x-4">
        {/* Project Dropdown */}
        <div className="relative">
          <select
            value={project}
            onChange={(e) => setProject(e.target.value)}
            className="bg-card text-text_primary rounded-md px-4 py-2 border border-gray-700 focus:outline-none focus:ring-2 focus:ring-brand min-w-48"
          >
            {projects.map((proj) => (
              <option key={proj} value={proj}>
                {proj}
              </option>
            ))}
          </select>
        </div>

        {/* Conversation Selector */}
        {activeTab === "Chat" && (
          <div className="relative">
            <button
              onClick={() => setIsConversationOpen(!isConversationOpen)}
              className="bg-card text-text_primary rounded-md px-4 py-2 border border-gray-700 focus:outline-none focus:ring-2 focus:ring-brand flex items-center space-x-2 min-w-48 justify-between"
            >
              <span className="truncate">{currentConversation}</span>
              <svg
                className={`w-4 h-4 transition-transform duration-200 ${isConversationOpen ? "transform rotate-180" : ""}`}
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M19 9l-7 7-7-7"
                />
              </svg>
            </button>

            <AnimatePresence>
              {isConversationOpen && (
                <motion.div
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.2 }}
                  className="absolute z-10 mt-1 w-full bg-card border border-gray-700 rounded-md shadow-lg py-1"
                >
                  {directMessageChannels.length > 0 ? (
                    directMessageChannels.map((channel: Channel) => (
                      <button
                        key={channel.id}
                        onClick={() => {
                          setActiveChannel(channel.id);
                          setIsConversationOpen(false);
                        }}
                        className={`w-full text-left px-4 py-2 text-sm ${
                          activeChannel === channel.id
                            ? "bg-brand/20 text-brand"
                            : "text-text_primary hover:bg-gray-700/50"
                        }`}
                      >
                        <div className="flex items-center justify-between">
                          <span className="truncate">{channel.name}</span>
                          {channel.unreadCount > 0 && (
                            <span className="bg-brand text-white text-xs rounded-full h-5 w-5 flex items-center justify-center ml-2">
                              {channel.unreadCount}
                            </span>
                          )}
                        </div>
                      </button>
                    ))
                  ) : (
                    <div className="px-4 py-2 text-sm text-text_secondary">
                      No conversations
                    </div>
                  )}
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        )}
      </div>

      {/* Center: Search Bar */}
      <div className="flex-1 max-w-2xl px-4 relative">
        <div className="relative">
          <input
            type="text"
            placeholder="Search users..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onFocus={() => setIsSearchFocused(true)}
            onBlur={() => setTimeout(() => setIsSearchFocused(false), 200)}
            className="w-full bg-card/50 border border-gray-700 rounded-full py-2 px-4 text-text_primary placeholder-text_secondary focus:outline-none focus:ring-2 focus:ring-brand focus:border-transparent"
          />
          <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
            <svg
              className="h-5 w-5 text-text_secondary"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
              />
            </svg>
          </div>

          {/* Search Results Dropdown */}
          {isSearchFocused && searchQuery && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: 10 }}
              className="absolute z-50 mt-1 w-full bg-card border border-gray-700 rounded-lg shadow-lg overflow-hidden"
            >
              {filteredUsers.length > 0 ? (
                <div className="py-1">
                  {filteredUsers.map((user) => (
                    <button
                      key={user.id}
                      onMouseDown={() => handleUserSelect(user)}
                      className="w-full text-left px-4 py-2 text-sm text-text_primary hover:bg-gray-700/50 flex items-center space-x-3"
                    >
                      <div
                        className={`h-2 w-2 rounded-full ${
                          user.status === "online"
                            ? "bg-green-500"
                            : user.status === "away"
                              ? "bg-yellow-500"
                              : "bg-gray-500"
                        }`}
                      ></div>
                      <span>{user.name}</span>
                      <span className="ml-auto text-xs text-text_secondary">
                        {user.status === "online"
                          ? "Online"
                          : user.status === "away"
                            ? "Away"
                            : "Offline"}
                      </span>
                    </button>
                  ))}
                </div>
              ) : searchQuery ? (
                <div className="px-4 py-3 text-sm text-text_secondary">
                  No users found for "{searchQuery}"
                </div>
              ) : null}
            </motion.div>
          )}
        </div>
      </div>

      {/* Right: Status and BPM */}
      <div className="flex items-center space-x-6">
        {/* Status Selector */}
        <div className="relative">
          <button
            onClick={() => setIsStatusMenuOpen(!isStatusMenuOpen)}
            className="flex items-center space-x-2 focus:outline-none"
            onBlur={() => setTimeout(() => setIsStatusMenuOpen(false), 200)}
          >
            <div
              className={`w-3 h-3 rounded-full ${statusColors[currentUserStatus]}`}
            ></div>
            <span className="text-sm text-text_secondary">
              {statusLabels[currentUserStatus]}
            </span>
            <svg
              className={`w-4 h-4 text-text_secondary transition-transform ${isStatusMenuOpen ? "transform rotate-180" : ""}`}
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M19 9l-7 7-7-7"
              />
            </svg>
          </button>

          {/* Status Dropdown */}
          <AnimatePresence>
            {isStatusMenuOpen && (
              <motion.div
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 10 }}
                className="absolute right-0 mt-2 w-40 bg-card border border-gray-700 rounded-lg shadow-lg overflow-hidden z-50"
              >
                <div className="py-1">
                  {(["online", "away", "offline"] as Status[]).map((status) => (
                    <button
                      key={status}
                      onClick={() => handleStatusChange(status as Status)}
                      className="w-full text-left px-4 py-2 text-sm text-text_primary hover:bg-gray-700/50 flex items-center space-x-2"
                    >
                      <div
                        className={`w-2 h-2 rounded-full ${statusColors[status]}`}
                      ></div>
                      <span>{statusLabels[status]}</span>
                      {currentUserStatus === status && (
                        <svg
                          className="w-4 h-4 ml-auto text-brand"
                          fill="none"
                          viewBox="0 0 24 24"
                          stroke="currentColor"
                        >
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth={2}
                            d="M5 13l4 4L19 7"
                          />
                        </svg>
                      )}
                    </button>
                  ))}
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* BPM Field */}
        <div className="flex items-center space-x-2">
          <span className="text-sm font-medium">BPM:</span>
          <input
            type="number"
            value={bpm}
            onChange={(e) =>
              setBpm(
                Math.max(40, Math.min(200, parseInt(e.target.value) || 120)),
              )
            }
            className="w-16 bg-card text-text_primary rounded-md px-2 py-1 border border-gray-700 focus:outline-none focus:ring-2 focus:ring-brand"
          />
        </div>
      </div>
    </header>
  );
};

export default TopBar;

import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useStore } from "@store";

type User = {
  id: string;
  name: string;
  avatar?: string;
  status: "online" | "offline" | "away" | "busy";
};

type CreateGroupModalProps = {
  isOpen: boolean;
  onClose: () => void;
};

const CreateGroupModal: React.FC<CreateGroupModalProps> = ({
  isOpen,
  onClose,
}) => {
  const { users, createGroup } = useStore();
  const [groupName, setGroupName] = useState("");
  const [description, setDescription] = useState("");
  const [selectedUsers, setSelectedUsers] = useState<string[]>([]);
  const [isPrivate, setIsPrivate] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  const currentUserId = "user1"; // In a real app, this would come from auth
  const otherUsers = users.filter((user) => user.id !== currentUserId);

  const filteredUsers = otherUsers.filter((user) =>
    user.name.toLowerCase().includes(searchQuery.toLowerCase()),
  );

  const handleUserToggle = (userId: string) => {
    setSelectedUsers((prev) =>
      prev.includes(userId)
        ? prev.filter((id) => id !== userId)
        : [...prev, userId],
    );
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!groupName.trim() || selectedUsers.length === 0) return;

    createGroup({
      name: groupName,
      participants: selectedUsers,
      description: description.trim() || undefined,
      isPrivate,
      // In a real app, you might want to allow users to upload an avatar
      avatar: `https://ui-avatars.com/api/?name=${encodeURIComponent(groupName)}&background=random`,
    });

    // Reset form
    setGroupName("");
    setDescription("");
    setSelectedUsers([]);
    setIsPrivate(false);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <AnimatePresence>
      <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50 p-4">
        <motion.div
          className="bg-panel rounded-xl shadow-2xl w-full max-w-md max-h-[90vh] flex flex-col overflow-hidden"
          initial={{ opacity: 0, scale: 0.95, y: 20 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          exit={{ opacity: 0, scale: 0.95, y: 20 }}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="p-6 pb-0">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-semibold text-text_primary">
                Create New Group
              </h2>
              <button
                onClick={onClose}
                className="text-text_secondary hover:text-text_primary transition-colors"
              >
                <svg
                  className="w-6 h-6"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
              </button>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label
                  htmlFor="groupName"
                  className="block text-sm font-medium text-text_secondary mb-1"
                >
                  Group Name *
                </label>
                <input
                  id="groupName"
                  type="text"
                  value={groupName}
                  onChange={(e) => setGroupName(e.target.value)}
                  className="w-full px-3 py-2 bg-card border border-gray-700 rounded-lg text-text_primary focus:outline-none focus:ring-2 focus:ring-brand"
                  placeholder="Enter group name"
                  required
                />
              </div>

              <div>
                <label
                  htmlFor="description"
                  className="block text-sm font-medium text-text_secondary mb-1"
                >
                  Description (Optional)
                </label>
                <textarea
                  id="description"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  className="w-full px-3 py-2 bg-card border border-gray-700 rounded-lg text-text_primary focus:outline-none focus:ring-2 focus:ring-brand"
                  placeholder="What's this group about?"
                  rows={2}
                />
              </div>

              <div>
                <div className="flex items-center justify-between mb-1">
                  <label className="text-sm font-medium text-text_secondary">
                    Add Members
                  </label>
                  <span className="text-xs text-text_secondary">
                    {selectedUsers.length} selected
                  </span>
                </div>
                <div className="relative">
                  <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="w-full px-3 py-2 bg-card border border-gray-700 rounded-lg text-text_primary focus:outline-none focus:ring-2 focus:ring-brand mb-2"
                    placeholder="Search users..."
                  />
                  <div className="max-h-48 overflow-y-auto bg-card border border-gray-700 rounded-lg divide-y divide-gray-700">
                    {filteredUsers.length > 0 ? (
                      filteredUsers.map((user) => (
                        <div
                          key={user.id}
                          className={`flex items-center p-3 hover:bg-gray-800 cursor-pointer ${selectedUsers.includes(user.id) ? "bg-gray-800" : ""}`}
                          onClick={() => handleUserToggle(user.id)}
                        >
                          <div className="relative">
                            <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center text-white">
                              {user.name.charAt(0).toUpperCase()}
                            </div>
                            <span
                              className={`absolute bottom-0 right-0 w-3 h-3 rounded-full border-2 border-panel ${
                                user.status === "online"
                                  ? "bg-green-500"
                                  : user.status === "away"
                                    ? "bg-yellow-500"
                                    : user.status === "busy"
                                      ? "bg-red-500"
                                      : "bg-gray-500"
                              }`}
                            ></span>
                          </div>
                          <div className="ml-3">
                            <p className="text-sm font-medium text-text_primary">
                              {user.name}
                            </p>
                            <p className="text-xs text-text_secondary">
                              {user.status === "online"
                                ? "Online"
                                : user.status === "away"
                                  ? "Away"
                                  : user.status === "busy"
                                    ? "Busy"
                                    : "Offline"}
                            </p>
                          </div>
                          <div className="ml-auto">
                            <div
                              className={`w-5 h-5 rounded border-2 ${
                                selectedUsers.includes(user.id)
                                  ? "bg-brand border-brand"
                                  : "border-gray-600"
                              } flex items-center justify-center`}
                            >
                              {selectedUsers.includes(user.id) && (
                                <svg
                                  className="w-3 h-3 text-white"
                                  fill="currentColor"
                                  viewBox="0 0 20 20"
                                >
                                  <path
                                    fillRule="evenodd"
                                    d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z"
                                    clipRule="evenodd"
                                  />
                                </svg>
                              )}
                            </div>
                          </div>
                        </div>
                      ))
                    ) : (
                      <div className="p-4 text-center text-text_secondary text-sm">
                        No users found
                      </div>
                    )}
                  </div>
                </div>
              </div>

              <div className="flex items-center">
                <input
                  type="checkbox"
                  id="isPrivate"
                  checked={isPrivate}
                  onChange={(e) => setIsPrivate(e.target.checked)}
                  className="h-4 w-4 text-brand focus:ring-brand border-gray-700 rounded"
                />
                <label
                  htmlFor="isPrivate"
                  className="ml-2 block text-sm text-text_secondary"
                >
                  Make this group private
                </label>
              </div>

              <div className="pt-4 flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={onClose}
                  className="px-4 py-2 text-sm font-medium text-text_secondary hover:text-text_primary rounded-lg transition-colors"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={!groupName.trim() || selectedUsers.length === 0}
                  className={`px-4 py-2 text-sm font-medium text-white rounded-lg transition-colors ${
                    !groupName.trim() || selectedUsers.length === 0
                      ? "bg-gray-600 cursor-not-allowed"
                      : "bg-brand hover:bg-brand/90"
                  }`}
                >
                  Create Group
                </button>
              </div>
            </form>
          </div>
        </motion.div>
      </div>
    </AnimatePresence>
  );
};

export default CreateGroupModal;

import React from "react";
import { motion, AnimatePresence } from "framer-motion";
// Store is not needed here as we're using props
// import { useStore } from '../../store';

export type Tab = "chat" | "files" | "history" | "account" | "friends";

interface NavbarProps {
  activeTab: Tab;
  onTabChange: (tab: Tab) => void;
}

const Navbar: React.FC<NavbarProps> = ({
  activeTab,
  onTabChange,
}: NavbarProps) => {
  const tabs: { id: Tab; icon: string; label: string }[] = [
    { id: "chat", icon: "", label: "Chat" },
    { id: "friends", icon: "", label: "Friends" },
    { id: "files", icon: "", label: "Files" },
    { id: "history", icon: "", label: "History" },
    { id: "account", icon: "", label: "Account" },
  ];

  return (
    <div className="flex flex-col items-center py-4 px-2 bg-panel border-r border-gray-700 h-full">
      <div className="space-y-2 w-full">
        {tabs.map((tab) => {
          const isActive = activeTab === tab.id;
          return (
            <motion.button
              key={tab.id}
              onClick={() => onTabChange(tab.id)}
              className={`relative flex flex-col items-center justify-center w-14 h-14 rounded-lg transition-colors ${
                isActive
                  ? "bg-brand/20 text-brand"
                  : "text-text_secondary hover:bg-gray-700/50"
              }`}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <span className="text-2xl">{tab.icon}</span>
              <span className="text-xs mt-1">{tab.label}</span>
              {isActive && (
                <motion.div
                  layoutId="nav-indicator"
                  className="absolute left-0 top-0 bottom-0 w-1 bg-brand rounded-r-md"
                  initial={false}
                  transition={{
                    type: "spring",
                    stiffness: 500,
                    damping: 30,
                  }}
                />
              )}
              {tab.id === "friends" && (
                <div className="absolute -top-1 -right-1 w-2 h-2 bg-red-500 rounded-full"></div>
              )}
            </motion.button>
          );
        })}
      </div>
    </div>
  );
};

export default Navbar;

// @ts-nocheck
import * as React from "react";
import { useState } from "react";
import { useStore } from "@store";
import { motion, AnimatePresence } from "framer-motion";
import CreateGroupModal from "./CreateGroupModal";

type User = {
  id: string;
  name: string;
  status: "online" | "offline" | "away" | "busy";
};

type Channel = {
  id: string;
  name: string;
  type: "direct" | "group" | "project";
  participants: string[];
  unreadCount?: number;
};

const Sidebar = () => {
  const [isCreateGroupModalOpen, setIsCreateGroupModalOpen] = useState(false);
  const { channels, users, activeChannel, setActiveChannel, activeTab } =
    useStore();

  // Separate channels by type
  const projectChannels = channels.filter((c) => c.type === "project");
  const groupChannels = channels.filter((c) => c.type === "group");
  const directMessageChannels = channels.filter((c) => c.type === "direct");

  const getChannelDisplayName = (channel: Channel) => {
    if (channel.type === "direct") {
      const user = users.find((u) => u.id === channel.id);
      return user?.name || channel.name;
    }
    return channel.name;
  };

  const getChannelStatus = (channel: Channel) => {
    if (channel.type === "direct") {
      const user = users.find((u) => u.id === channel.id);
      return user?.status || "offline";
    }
    return null;
  };

  const renderChannelItem = (channel: Channel) => (
    <motion.div
      key={channel.id}
      whileHover={{ backgroundColor: "rgba(255, 255, 255, 0.05)" }}
      className={`px-4 py-2 cursor-pointer flex items-center justify-between ${
        activeChannel === channel.id ? "bg-brand/10" : ""
      }`}
      onClick={() => setActiveChannel(channel.id)}
    >
      <div className="flex items-center space-x-2 truncate">
        {channel.type === "direct" ? (
          <div className="relative flex-shrink-0">
            <div
              className={`w-2 h-2 rounded-full ${
                getChannelStatus(channel) === "online"
                  ? "bg-green-500"
                  : getChannelStatus(channel) === "away"
                    ? "bg-yellow-500"
                    : getChannelStatus(channel) === "busy"
                      ? "bg-red-500"
                      : "bg-gray-500"
              } absolute -left-3 top-1/2 transform -translate-y-1/2`}
            />
            <span className="text-text_primary truncate">
              {getChannelDisplayName(channel)}
            </span>
          </div>
        ) : (
          <div className="flex items-center space-x-2 truncate">
            {channel.type === "project" ? (
              <span className="text-text_secondary">#</span>
            ) : (
              <span className="text-text_secondary">
                <svg
                  className="w-4 h-4"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z"
                  />
                </svg>
              </span>
            )}
            <span className="text-text_primary truncate">
              {getChannelDisplayName(channel)}
            </span>
          </div>
        )}
      </div>
      {channel.unreadCount > 0 && (
        <span className="bg-brand text-white text-xs rounded-full h-5 w-5 flex-shrink-0 flex items-center justify-center">
          {channel.unreadCount}
        </span>
      )}
    </motion.div>
  );

  if (activeTab !== "Chat") {
    return null;
  }

  return (
    <div className="w-64 border-r border-gray-700 bg-panel flex flex-col h-full">
      {/* Header */}
      <div className="p-4 border-b border-gray-700 flex justify-between items-center">
        <h2 className="text-lg font-semibold text-text_primary">Channels</h2>
        <button
          onClick={() => setIsCreateGroupModalOpen(true)}
          className="text-text_secondary hover:text-brand transition-colors p-1"
          title="Create new group"
        >
          <svg
            className="w-5 h-5"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M12 6v6m0 0v6m0-6h6m-6 0H6"
            />
          </svg>
        </button>
      </div>

      {/* Project Channels */}
      {projectChannels.length > 0 && (
        <div className="mt-2">
          <div className="px-4 py-2 text-xs font-medium text-text_secondary uppercase tracking-wider">
            Projects
          </div>
          <div className="space-y-1">
            {projectChannels.map((channel) => renderChannelItem(channel))}
          </div>
        </div>
      )}

      {/* Group Chats */}
      <div className="mt-2">
        <div className="px-4 py-2 flex items-center justify-between">
          <span className="text-xs font-medium text-text_secondary uppercase tracking-wider">
            Group Chats
