declare module "react" {
  export = React;
  export as namespace React;

  namespace React {
    // Type definitions for React
    interface FunctionComponent<P = {}> {
      (props: P, context?: unknown): ReactElement<unknown, unknown> | null;
      propTypes?: WeakValidationMap<P> | undefined;
      contextTypes?: ValidationMap<unknown> | undefined;
      defaultProps?: Partial<P> | undefined;
      displayName?: string | undefined;
    }

    interface ReactElement<
      P = unknown,
      T extends string | JSXElementConstructor<unknown> =
        | string
        | JSXElementConstructor<unknown>,
    > {
      type: T;
      props: P;
      key: Key | null;
    }

    type JSXElementConstructor<P> =
      | ((props: P) => ReactElement<unknown, unknown> | null)
      | (new (props: P) => Component<P, unknown>);

    type Key = string | number;

    interface Component<P = {}, S = {}, SS = unknown>
      extends ComponentLifecycle<P, S, SS> {}

    interface ComponentLifecycle<P, S, SS = unknown> {
      componentDidMount?(): void;
      shouldComponentUpdate?(
        nextProps: Readonly<P>,
        nextState: Readonly<S>,
        nextContext: unknown,
      ): boolean;
      componentWillUnmount?(): void;
      componentDidCatch?(error: Error, errorInfo: ErrorInfo): void;
    }

    interface ErrorInfo {
      componentStack: string;
    }
  }
}

import { ExecutionContext, Injectable, UnauthorizedException } from '@nestjs/common';
import { AuthGuard } from '@nestjs/passport';
import { Request } from 'express';

// Extend the Express Request type to include the user property
declare global {
  namespace Express {
    interface User {
      userId: string;
      email: string;
      name?: string;
    }

    interface Request {
      user?: User;
    }
  }
}

@Injectable()
export class JwtAuthGuard extends AuthGuard('jwt') {
  canActivate(context: ExecutionContext) {
    return super.canActivate(context);
  }

  handleRequest(err: any, user: any, info: any, context: ExecutionContext) {
    if (err || !user) {
      throw err || new UnauthorizedException('Authentication failed');
    }
    const request = context.switchToHttp().getRequest<Request>();
    request.user = user;
    return user;
  }
}

import { ExecutionContext, Injectable } from '@nestjs/common';
import { JwtService } from '@nestjs/jwt';
import { WsException } from '@nestjs/websockets';
import { Socket } from 'socket.io';

@Injectable()
export class JwtWsAuthGuard {
  constructor(private readonly jwt: JwtService) {}

  async canActivate(context: ExecutionContext): Promise<boolean> {
    const client = context.switchToWs().getClient<Socket>();
    const token = client.handshake.auth?.token;

    if (!token) {
      throw new WsException('Missing token');
    }

    try {
      const payload: any = this.jwt.verify(token);

      // Add user to handshake
      (client.handshake as any).user = {
        userId: payload.sub,
        email: payload.email,
        name: payload.name,
      };

      return true;
    } catch (error) {
      throw new WsException('Invalid token');
    }
  }
}

import { ExtractJwt, Strategy, StrategyOptions } from 'passport-jwt';
import { PassportStrategy } from '@nestjs/passport';
import { Injectable, UnauthorizedException } from '@nestjs/common';
import { ConfigService } from '@nestjs/config';
import { PrismaService } from '../../prisma/prisma.service';
import * as bcrypt from 'bcryptjs';
import { Request } from 'express';

interface JwtPayload {
  sub: string;
  email: string;
}

@Injectable()
export class RefreshTokenStrategy extends PassportStrategy(
  Strategy,
  'jwt-refresh',
) {
  constructor(
    private prisma: PrismaService,
    configService: ConfigService,
  ) {
    const secret = configService.get<string>('JWT_REFRESH_SECRET');
    if (!secret) {
      throw new Error('JWT_REFRESH_SECRET is not defined in the configuration');
    }

    const strategyOptions: StrategyOptions = {
      jwtFromRequest: ExtractJwt.fromExtractors([
        (req: Request) => {
          return req?.cookies?.refreshToken;
        },
      ]),
      secretOrKey: secret,
      passReqToCallback: true,
    };

    super(strategyOptions as any); // Type assertion to bypass the type checking issue
  }

  async validate(req: Request, payload: JwtPayload) {
    const refreshToken = req.cookies?.refreshToken;
    if (!refreshToken) {
      throw new UnauthorizedException('Refresh token not found');
    }

    const user = await this.prisma.user.findUnique({
      where: { id: payload.sub },
    });

    if (!user?.refreshToken) {
      throw new UnauthorizedException('Refresh token is invalid');
    }

    const isTokenValid = await bcrypt.compare(refreshToken, user.refreshToken);
    if (!isTokenValid) {
      throw new UnauthorizedException('Refresh token is invalid');
    }

    return { ...payload, refreshToken };
  }
}

