import { Injectable, Logger, OnModuleInit } from '@nestjs/common';
import { Prisma } from '@prisma/client';
import { PrismaService } from '../../prisma/prisma.service';
import { WebSocketGateway, WebSocketServer } from '@nestjs/websockets';
import { Server } from 'socket.io';
import { LogActivityDto } from './dto/log-activity.dto';
import { EventEmitter2 } from '@nestjs/event-emitter';

// Type for raw activity log from database
type RawActivityLog = {
  id: string;
  action: string;
  entityType: string;
  entityId: string;
  metadata: any; // Using any to avoid Prisma type issues
  ipAddress: string | null;
  userAgent: string | null;
  userId: string;
  projectId: string | null;
  createdAt: Date;
};

// Type for activity log with user data
type ActivityWithUser = {
  id: string;
  action: string;
  entityType: string;
  entityId: string;
  metadata: any; // Using any to avoid Prisma type issues
  ipAddress: string | null;
  userAgent: string | null;
  createdAt: Date;
  userId: string;
  projectId: string | null;
  user: {
    id: string;
    name: string | null;
    email: string;
  } | null;
};

@Injectable()
@WebSocketGateway({ namespace: 'activity' })
export class ActivityLoggerService implements OnModuleInit {
  private readonly logger = new Logger(ActivityLoggerService.name);

  @WebSocketServer()
  private server!: Server;

  constructor(
    private prisma: PrismaService,
    private eventEmitter: EventEmitter2
  ) {}

  onModuleInit() {
    this.logger.log('Activity Logger Service initialized');
  }

  /**
   * Log an activity and emit a WebSocket event
   * @param data Activity data to log
   * @returns The created activity log
   */
  async logActivity(data: LogActivityDto): Promise<ActivityWithUser> {
    const { 
      action, 
      entityId = '', 
      entityType = 'system',
      userId,
      projectId,
      metadata = {},
      ipAddress,
      userAgent
    } = data;

    try {
      // Create activity data
      // Build activity data with optional fields
      const activityData: unknown = {
        action,
        entityId,
        entityType,
        metadata: metadata as unknown, // Using unknown to avoid Prisma type issues
        ...(ipAddress && { ipAddress }),
        ...(userAgent && { userAgent }),
        // userId is optional, only include if provided
        ...(userId && { userId }),
        ...(projectId && { projectId }),
      };

      // Create the activity log with all necessary fields using raw query
      const query = `
        INSERT INTO "ActivityLog" (${Object.keys(activityData).join(', ')})
        VALUES (${Object.keys(activityData).map((_, i) => `$${i + 1}`).join(', ')})
        RETURNING 
          id, action, "entityType" as "entityType", "entityId" as "entityId", 
          metadata, "ipAddress" as "ipAddress", "userAgent" as "userAgent", 
          "userId" as "userId", "projectId" as "projectId", "createdAt" as "createdAt"
      `;

      const values = Object.values(activityData);
      const activity = (await this.prisma.$queryRawUnsafe(query, ...values) as RawActivityLog[])[0];

      // Fetch user data if userId is provided
      let user: { id: string; name: string | null; email: string } | null = null;
      if (userId) {
        user = await this.prisma.user.findUnique({
          where: { id: userId },
          select: {
            id: true,
            name: true,
            email: true,
          },
        });
      }

      // Map to our ActivityWithUser type
      const activityWithUser: ActivityWithUser = {
        id: activity.id,
        action: activity.action,
        entityType: activity.entityType,
        entityId: activity.entityId,
        metadata: activity.metadata,
        createdAt: activity.createdAt,
        ipAddress: activity.ipAddress,
        userAgent: activity.userAgent,
        userId: activity.userId,
        projectId: activity.projectId,
        user: user,
      };

      // Emit WebSocket event
      if (this.server) {
        const room = projectId ? `project:${projectId}` : `user:${userId || 'system'}`;
        const eventData = {
          ...activityWithUser,
          user: activityWithUser.user || null,
          projectId: activityWithUser.projectId || null,
          userId: activityWithUser.userId || null
        } as const;

        // Emit to the specific room (project or user)
        this.server.to(room).emit('activity', eventData);

        // Also emit to the global namespace
        this.server.emit('activity', eventData);
      }

      // Emit a system event for other services
      this.eventEmitter.emit('activity.created', activityWithUser);

      this.logger.debug(`Activity logged: ${action} for project ${projectId} by user ${userId}`);

      return activityWithUser;
    } catch (error) {
      this.logger.error(`Failed to log activity: ${typeof error === 'object' && error && 'message' in error ? (error as { message: string }).message : String(error)}`, typeof error === 'object' && error && 'stack' in error ? (error as { stack: string }).stack : undefined);
      throw error;
    }
  }

  /**
   * Get recent activities for a project or user
   * @param options Query options
   * @returns List of activity logs with user data
   */
  async getActivities(options: {
    projectId?: string;
    userId?: string | null;
    action?: string;
    entityType?: string;
    entityId?: string;
    limit?: number;
    cursor?: string;
  }): Promise<ActivityWithUser[]> {
    const { 
      projectId, 
      userId, 
      action, 
      entityType,
      entityId,
      limit = 50, 
      cursor 
    } = options;

    // Build the WHERE clause
    const whereClauses: string[] = [];
    const params: unknown[] = [];
    let paramIndex = 1;

    if (projectId) {
      whereClauses.push(`"projectId" = $${paramIndex++}`);
      params.push(projectId);
    }

    if (userId) {
      whereClauses.push(`"userId" = $${paramIndex++}`);
      params.push(userId);
    }

    if (action) {
      whereClauses.push(`"action" = $${paramIndex++}`);
      params.push(action);
    }

    if (entityType) {
      whereClauses.push(`"entityType" = $${paramIndex++}`);
      params.push(entityType);
    }

    if (entityId) {
      whereClauses.push(`"entityId" = $${paramIndex++}`);
      params.push(entityId);
    }

    if (cursor) {
      whereClauses.push(`id < $${paramIndex++}`);
      params.push(cursor);
    }

    const whereClause = whereClauses.length > 0 
      ? `WHERE ${whereClauses.join(' AND ')}` 
      : '';

    // Get activities with user data
    const query = `
      SELECT 
        a.id, a.action, a."entityType", a."entityId", a.metadata, 
        a."ipAddress", a."userAgent", a."userId", a."projectId", a."createdAt",
        u.id as "user.id", u.name as "user.name", u.email as "user.email"
      FROM "ActivityLog" a
      LEFT JOIN "User" u ON a."userId" = u.id
      ${whereClause}
      ORDER BY a."createdAt" DESC
      LIMIT $${paramIndex++}
    `;

    params.push(limit);

    const activities = await this.prisma.$queryRawUnsafe(query, ...params) as Array<RawActivityLog & {
      "user.id"?: string;
      "user.name"?: string | null;
      "user.email"?: string;
    }>;

    // Map the raw results to ActivityWithUser objects
    return activities.map(row => ({
      id: row.id,
      action: row.action,
      entityType: row.entityType,
      entityId: row.entityId,
      metadata: row.metadata,
      ipAddress: row.ipAddress,
      userAgent: row.userAgent,
      userId: row.userId,
      projectId: row.projectId,
      createdAt: row.createdAt,
      user: row["user.id"] ? {
        id: row["user.id"]!,
        name: row["user.name"] || null,
        email: row["user.email"]!,
      } : null,
    }));
  }

  /**
   * Get a summary of recent activities
   * @param projectId Project ID
   * @param days Number of days to look back
   * @returns Activity summary
   */
  async getActivitySummary(projectId: string, days = 7) {
    const date = new Date();
    date.setDate(date.getDate() - days);

    const query = `
      SELECT 
        DATE("createdAt") as date,
        "action" as event,
        COUNT(*) as count
      FROM "ActivityLog"
      WHERE "projectId" = $1
        AND "createdAt" >= $2
      GROUP BY DATE("createdAt"), "action"
      ORDER BY date DESC, count DESC;
    `;

    return this.prisma.$queryRawUnsafe(query, projectId, date);
  }
}

import { 
  Controller, 
  Post, 
  Body, 
  HttpStatus, 
  UsePipes, 
  ValidationPipe, 
  Res, 
  Get, 
  Req,
  UseGuards,
  HttpCode
} from '@nestjs/common';
import type { Request, Response } from 'express';
import { AuthService, AuthResponse } from './auth.service';
import { RegisterDto } from './dto/register.dto';
import { LoginDto } from './dto/login.dto';
import { JwtAuthGuard } from './guards/jwt-auth.guard';
import { RefreshTokenGuard } from './guards/refresh-token.guard';
import { GetCurrentUserId } from './decorators/get-current-user-id.decorator';
import { GetCurrentUser } from './decorators/get-current-user.decorator';

@Controller('auth')
@UsePipes(new ValidationPipe({ whitelist: true, transform: true }))
export class AuthController {
  constructor(private readonly authService: AuthService) {}

  @Post('register')
  async register(
    @Body() registerDto: RegisterDto,
    @Res({ passthrough: true }) res: Response,
  ): Promise<AuthResponse> {
    const result = await this.authService.signup(
      registerDto.email,
      registerDto.password,
      registerDto.name,
    );

    this.authService.setRefreshTokenCookie(res, result.refreshToken);

    return result;
  }

  @Post('login')
  @HttpCode(HttpStatus.OK)
  async login(
    @Body() loginDto: LoginDto,
    @Res({ passthrough: true }) res: Response,
  ): Promise<AuthResponse> {
    const result = await this.authService.login(loginDto.email, loginDto.password);

    this.authService.setRefreshTokenCookie(res, result.refreshToken);

    return result;
  }

  @UseGuards(RefreshTokenGuard)
  @Post('refresh')
  @HttpCode(HttpStatus.OK)
  async refreshTokens(
    @GetCurrentUserId() userId: string,
    @GetCurrentUser('refreshToken') refreshToken: string,
    @Res({ passthrough: true }) res: Response,
  ): Promise<AuthResponse> {
    const tokens = await this.authService.refreshTokens(userId, refreshToken);
    const { id, email, name } = await this.authService.getUserById(userId);
    return { ...tokens, user: { id, email, name } } as const;
  }

  @UseGuards(JwtAuthGuard)
  @Post('logout')
  @HttpCode(HttpStatus.NO_CONTENT)
  async logout(
    @GetCurrentUserId() userId: string,
    @Res({ passthrough: true }) res: Response,
  ): Promise<boolean> {
    await this.authService.logout(userId);
    this.authService.clearRefreshTokenCookie(res);
    return true;
  }

  @UseGuards(JwtAuthGuard)
  @Get('me')
  getProfile(@Req() req: Request) {
    return req.user;
  }
}

import { 
  Controller, 
  Post, 
  Body, 
  HttpStatus, 
  HttpException, 
  UsePipes, 
  ValidationPipe, 
  Req, 
  Res, 
  UseGuards, 
  Get, 
  HttpCode 
} from '@nestjs/common';
import type { Request, Response } from 'express';
import { 
  ApiTags, 
  ApiOperation, 
  ApiResponse, 
  ApiBody, 
  ApiBearerAuth 
} from '@nestjs/swagger';
import { AuthService, AuthResponse, Tokens } from './auth.service';
import { RegisterDto } from './dto/register.dto';
import { LoginDto } from './dto/login.dto';
import { LocalAuthGuard } from './guards/local-auth.guard';
import { RefreshTokenGuard } from './guards/refresh-token.guard';
import { JwtAuthGuard } from './guards/jwt-auth.guard';

@Controller('auth')
@UsePipes(new ValidationPipe({ whitelist: true, transform: true }))
export class AuthController {
  constructor(private readonly authService: AuthService) {}

  @Post('signup')
  async signup(
    @Body() registerDto: RegisterDto,
    @Res({ passthrough: true }) res: Response,
  ) {
    try {
      const result = await this.authService.signup(
        registerDto.email,
        registerDto.password,
        registerDto.name,
      );

      // Set refresh token in HTTP-only cookie
      this.authService.setRefreshTokenCookie(res, result.refreshToken);

      return {
        accessToken: result.accessToken,
        user: {
          id: result.user.id,
          email: result.user.email,
          name: result.user.name || result.user.email.split('@')[0] // Use email prefix as fallback for name
        }
      };
    } catch (error) {
      throw new HttpException(
        (error as unknown as Error).message || 'Registration failed',
        HttpStatus.UNPROCESSABLE_ENTITY,
      );
    }
  }

  @Post('register')
  async register(
    @Body() registerDto: RegisterDto,
    @Res({ passthrough: true }) res: Response,
  ) {
    return this.signup(registerDto, res);
  }

  @Post('login')
  @UseGuards(LocalAuthGuard)
  @ApiOperation({ summary: 'Login with email and password' })
  @ApiResponse({ status: 200, description: 'Login successful' })
  @ApiResponse({ status: 401, description: 'Invalid credentials' })
  @ApiBody({ type: LoginDto })
  async login(
    @Body() loginDto: LoginDto,
    @Res({ passthrough: true }) res: Response,
  ): Promise<{ accessToken: string; user: { id: string; email: string; name: string } }> {
    const { email, password } = loginDto;
    const result = await this.authService.login(email, password);
    this.authService.setRefreshTokenCookie(res, result.refreshToken);

    return {
      accessToken: result.accessToken,
      user: {
        id: result.user.id,
        email: result.user.email,
        name: result.user.name || result.user.email.split('@')[0], // Use email prefix as fallback for name
      },
    };
  }

  @UseGuards(RefreshTokenGuard)
  @Post('refresh')
  async refreshToken(
    @Req() req: unknown, 
    @Res({ passthrough: true }) res: Response
  ) {
    const userId = (req as any).user.sub;
    const refreshToken = (req as any).cookies.refreshToken;

    if (!refreshToken) {
      throw new HttpException('Refresh token is required', HttpStatus.BAD_REQUEST);
    }

    const tokens = await this.authService.refreshTokens(userId, refreshToken);
    this.authService.setRefreshTokenCookie(res, tokens.refreshToken);

    return { 
      accessToken: tokens.accessToken,
      user: tokens.user
    };
  }

  @UseGuards(JwtAuthGuard)
  @Post('logout')
  @HttpCode(HttpStatus.OK)
  async logout(
    @Req() req: unknown, 
    @Res({ passthrough: true }) res: Response
  ) {
    await this.authService.logout((req as any).user.id);
    this.authService.clearRefreshTokenCookie(res);
    return { message: 'Logged out successfully' };
  }

  @UseGuards(JwtAuthGuard)
  @Get('profile')
  getProfile(@Req() req: unknown) {
    return (req as any).user;
  }
}

import { 
  Injectable, 
  UnauthorizedException, 
  ConflictException,
  ForbiddenException,
  Logger
} from '@nestjs/common';
import { JwtService } from '@nestjs/jwt';
// Import PrismaService and types
import { PrismaService } from '../../prisma/prisma.service';

// Define the user data we want to expose
interface SafeUser {
  id: string;
  email: string;
  name: string | null;
}
import * as bcrypt from 'bcryptjs';
import { ConfigService } from '@nestjs/config';
import { Response } from 'express';

export interface TokenPayload {
  sub: string;
  email: string;
  name?: string;
  [key: string]: any;
}

export interface AuthResponse {
  accessToken: string;
  refreshToken: string;
  user: {
    id: string;
    email: string;
    name: string | null;
  };
}

export interface Tokens {
  accessToken: string;
  refreshToken: string;
  user: {
    id: string;
    email: string;
    name: string | null;
  };
}

@Injectable()
export class AuthService {
  private readonly logger = new Logger(AuthService.name);
  private readonly SALT_ROUNDS = 12;
  private readonly REFRESH_TOKEN_EXPIRY = '7d';
  private readonly ACCESS_TOKEN_EXPIRY = '15m';

  constructor(
    private readonly jwtService: JwtService,
    private readonly prisma: PrismaService,
    private readonly configService: ConfigService,
  ) {}

  async signup(email: string, password: string, name?: string): Promise<AuthResponse> {
    try {
      // Check if user already exists
      const existingUser = await this.prisma.user.findUnique({ where: { email } });
      if (existingUser) {
        throw new ConflictException('Email already in use');
      }

      // Hash password
      const hashedPassword = await bcrypt.hash(password, this.SALT_ROUNDS);

      // Create user with default values
      const user = await this.prisma.user.create({
        data: {
          email,
          name: name || email.split('@')[0], // Use email prefix as default name
          password: hashedPassword,
          isApproved: true, // Default to approved
        },
      });

      // Generate tokens
      const tokens = await this.getTokens(user.id, user.email, user.name || '');

      // Update refresh token in database
      await this.updateRefreshToken(user.id, tokens.refreshToken);

      return {
        accessToken: tokens.accessToken,
        refreshToken: tokens.refreshToken,
        user: {
          id: user.id,
          email: user.email,
          name: user.name,
        },
      };
    } catch (error) {
      this.logger.error(`Signup error: ${(error as any).message}`, (error as any).stack);
      throw error;
    }
  }

  async login(email: string, password: string): Promise<AuthResponse> {
    try {
      // Find user by email
      const user = await this.prisma.user.findUnique({ where: { email } });
      if (!user) {
        throw new UnauthorizedException('Invalid credentials');
      }

      // Verify password
      const isPasswordValid = await bcrypt.compare(password, user.password);
      if (!isPasswordValid) {
        throw new UnauthorizedException('Invalid credentials');
      }

      // Generate tokens
      const tokens = await this.getTokens(user.id, user.email, user.name || '');

      // Update refresh token in database
      await this.updateRefreshToken(user.id, tokens.refreshToken);

      return {
        accessToken: tokens.accessToken,
        refreshToken: tokens.refreshToken,
        user: {
          id: user.id,
          email: user.email,
          name: user.name,
        },
      };
    } catch (error) {
      this.logger.error(`Login error: ${(error as any).message}`, (error as any).stack);
      throw error;
    }
  }

  async logout(userId: string): Promise<boolean> {
    try {
      await this.prisma.user.updateMany({
        where: { id: userId, refreshToken: { not: null } },
        data: { refreshToken: null },
      });
      return true;
    } catch (error) {
      this.logger.error(`Logout error: ${(error as any).message}`, (error as any).stack);
      throw error;
    }
  }



  async refreshTokens(userId: string, refreshToken: string): Promise<Tokens> {
    if (!refreshToken) {
      throw new UnauthorizedException('Refresh token is required');
    }

    try {
      // Verify the refresh token
      const payload = this.jwtService.verify(refreshToken, {
        secret: this.configService.get<string>('JWT_REFRESH_SECRET'),
      });

      // Verify the user exists
      const user = await this.prisma.user.findUnique({
        where: { id: userId },
      });

      if (!user) {
        throw new UnauthorizedException('User not found');
      }

      // Verify the token matches the one in the database
      if (user.refreshToken !== refreshToken) {
        throw new UnauthorizedException('Invalid refresh token');
      }

      // Generate new tokens
      const tokens = await this.getTokens(user.id, user.email, user.name || undefined);

      // Update the refresh token in the database
      await this.prisma.user.update({
        where: { id: user.id },
        data: { refreshToken: tokens.refreshToken },
      });

      return tokens;
    } catch (error) {
      this.logger.error(`Refresh token error: ${(error as any).message}`, (error as any).stack);
      throw new UnauthorizedException('Invalid refresh token');
    }
  }

  async validateUser(email: string, password: string): Promise<SafeUser | null> {
    const user = await this.prisma.user.findUnique({ 
      where: { email },
      select: { id: true, email: true, name: true, password: true }
    });

    if (!user) {
      return null;
    }

    const isPasswordValid = await bcrypt.compare(password, user.password);
    if (!isPasswordValid) {
      return null;
    }

    // Return only the safe user fields
    return {
      id: user.id,
      email: user.email,
      name: user.name
    };
  }

  async verifyToken(token: string): Promise<TokenPayload> {
    try {
      return this.jwtService.verify(token, {
        secret: this.configService.get<string>('JWT_SECRET'),
      });
    } catch (error) {
      this.logger.error(`Token verification failed: ${(error as any).message}`, (error as any).stack);
      throw new UnauthorizedException('Invalid token');
    }
  }

  private async updateRefreshToken(userId: string, refreshToken: string): Promise<void> {
    try {
      const hashedRefreshToken = await bcrypt.hash(refreshToken, this.SALT_ROUNDS);
      await this.prisma.user.update({
        where: { id: userId },
        data: { refreshToken: hashedRefreshToken },
      });
    } catch (error) {
      this.logger.error(`Update refresh token error: ${(error as any).message}`, (error as any).stack);
      throw error;
    }
  }

  private async revokeUserRefreshTokens(userId: string): Promise<void> {
    try {
      await this.prisma.user.updateMany({
        where: { id: userId },
        data: { refreshToken: null },
      });
    } catch (error) {
      this.logger.error(`Revoke refresh tokens error: ${(error as any).message}`, (error as any).stack);
      throw error;
    }
  }

  private async getTokens(userId: string, email: string, name: string): Promise<Tokens> {
    try {
      const [accessToken, refreshToken] = await Promise.all([
        this.jwtService.signAsync(
          { sub: userId, email, name },
          {
            secret: this.configService.get<string>('JWT_SECRET'),
            expiresIn: this.ACCESS_TOKEN_EXPIRY,
          },
        ),
        this.jwtService.signAsync(
          { sub: userId, email, name },
          {
            secret: this.configService.get<string>('JWT_REFRESH_SECRET'),
            expiresIn: this.REFRESH_TOKEN_EXPIRY,
          },
        ),
      ]);

      return {
        accessToken,
        refreshToken,
        user: {
          id: userId,
          email,
          name,
        },
      };
    } catch (error) {
      this.logger.error(`Failed to generate tokens: ${(error as any).message}`, (error as any).stack);
      throw error;
    }
  }

  setRefreshTokenCookie(res: Response, refreshToken: string): void {
    const isProduction = this.configService.get<string>('NODE_ENV') === 'production';
    res.cookie('refreshToken', refreshToken, {
      httpOnly: true,
      secure: isProduction,
      sameSite: isProduction ? 'strict' : 'lax',
      maxAge: 7 * 24 * 60 * 60 * 1000, // 7 days
      path: '/',
    });
  }

  clearRefreshTokenCookie(res: Response): void {
    const isProduction = this.configService.get<string>('NODE_ENV') === 'production';
    res.clearCookie('refreshToken', {
      httpOnly: true,
      secure: isProduction,
      sameSite: isProduction ? 'strict' : 'lax',
      path: '/',
    });
  }

  async getUserById(userId: string) {
    const user = await this.prisma.user.findUnique({
      where: { id: userId },
      select: { id: true, email: true, name: true }
    });

    if (!user) {
      throw new UnauthorizedException('User not found');
    }

    return user;
  }
}

