import { ExecutionContext, Injectable, UnauthorizedException } from '@nestjs/common';
import { AuthGuard } from '@nestjs/passport';
import { WsException } from '@nestjs/websockets';
import { Observable } from 'rxjs';

@Injectable()
export class JwtWsAuthGuard extends AuthGuard('jwt') {
  getRequest(context: ExecutionContext) {
    const client = context.switchToWs().getClient();

    // For WebSocket connections, we need to extract the token from the handshake query
    const token = client.handshake?.auth?.token || 
                 client.handshake?.query?.token;

    if (!token) {
      throw new WsException('Missing authentication token');
    }

    // For WebSockets, we need to mock the HTTP request object
    return {
      headers: {
        authorization: `Bearer ${token}`,
      },
    };
  }

  handleRequest(err: unknown, user: unknown, info: unknown) {
    if (err || !user) {
      throw err || new WsException('Unauthorized');
    }
    return user;
  }

  canActivate(
    context: ExecutionContext,
  ): boolean | Promise<boolean> | Observable<boolean> {
    // Add your custom authentication logic here
    // for example, call super.logIn(request) to establish a session.
    return super.canActivate(context);
  }
}

import { ExecutionContext, Injectable } from '@nestjs/common';
import { AuthGuard } from '@nestjs/passport';
import { WsException } from '@nestjs/websockets';
import { JwtService } from '@nestjs/jwt';
import { ConfigService } from '@nestjs/config';

@Injectable()
export class WsJwtGuard extends AuthGuard('ws-jwt') {
  constructor(
    private jwtService: JwtService,
    private configService: ConfigService,
  ) {
    super();
  }

  getRequest(context: ExecutionContext) {
    const client = context.switchToWs().getClient();
    const token = this.extractTokenFromHeader(client);

    if (!token) {
      throw new WsException('Unauthorized');
    }

    try {
      const payload = this.jwtService.verify(token, {
        secret: this.configService.get('JWT_SECRET'),
      });

      // Attach user to the client for later use
      client.data = { user: payload };
      return client;
    } catch {
      throw new WsException('Invalid token');
    }
  }

  private extractTokenFromHeader(client: unknown): string | undefined {
    const [type, token] = (client as any).handshake?.auth?.token?.split(' ') ?? [];
    return type === 'Bearer' ? token : undefined;
  }
}

import { ExtractJwt, Strategy } from 'passport-jwt';
import { PassportStrategy } from '@nestjs/passport';
import { Injectable, UnauthorizedException } from '@nestjs/common';
import { ConfigService } from '@nestjs/config';
import { PrismaService } from '../../../prisma/prisma.service';
import * as bcrypt from 'bcryptjs';

interface JwtPayload {
  sub: string;
  email: string;
}

@Injectable()
export class JwtRefreshStrategy extends PassportStrategy(
  Strategy,
  'jwt-refresh',
) {
  private readonly authConfig: any;

  constructor(
    private readonly prisma: PrismaService,
    private readonly configService: ConfigService,
  ) {
    const authConfig = configService.get('auth', {
      refreshToken: {
        cookieName: 'refreshToken',
        secret: process.env.JWT_REFRESH_SECRET || 'defaultRefreshSecret',
      },
    });

    super({
      jwtFromRequest: (req: { cookies?: { [key: string]: string } }) => {
        const cookieName = authConfig?.refreshToken?.cookieName || 'refreshToken';
        return req?.cookies?.[cookieName];
      },
      secretOrKey: authConfig?.refreshToken?.secret || 'defaultRefreshSecret',
      passReqToCallback: true,
    } as any); // Using type assertion to bypass the type checking issue

    this.authConfig = {
      refreshToken: {
        cookieName: authConfig?.refreshToken?.cookieName || 'refreshToken',
        secret: authConfig?.refreshToken?.secret || 'defaultRefreshSecret',
      },
    };
  }

  async validate(req: any, payload: JwtPayload) {
    const refreshToken = req?.cookies?.[this.authConfig.refreshToken.cookieName];

    if (!refreshToken) {
      throw new UnauthorizedException('Refresh token not found');
    }

    const user = await this.prisma.user.findUnique({
      where: { id: payload.sub },
    });

    if (!user || !user.refreshToken) {
      throw new UnauthorizedException('Invalid refresh token');
    }

    const isRefreshTokenMatching = await bcrypt.compare(
      refreshToken,
      user.refreshToken,
    );

    if (!isRefreshTokenMatching) {
      throw new UnauthorizedException('Invalid refresh token');
    }

    return { userId: payload.sub, email: payload.email };
  }
}

import {
  WebSocketGateway,
  WebSocketServer,
  OnGatewayInit,
  OnGatewayConnection,
  OnGatewayDisconnect,
} from '@nestjs/websockets';
import { UseGuards, Logger } from '@nestjs/common';
import { Server, Socket } from 'socket.io';
import { createAdapter } from '@socket.io/redis-adapter';
import { createClient } from 'redis';
import { JwtWsAuthGuard } from '../auth/guards/jwt-ws-auth.guard';

@WebSocketGateway({ namespace: '/chat', cors: { origin: '*' } })
@UseGuards(JwtWsAuthGuard)
export class ChatGateway implements OnGatewayInit, OnGatewayConnection, OnGatewayDisconnect {
  private readonly logger = new Logger(ChatGateway.name);

  private _server!: Server;

  get server(): Server {
    return this._server;
  }

  async afterInit() {
    // Redis adapter for horizontal scaling
    const pubClient = createClient({ url: process.env.REDIS_URL || 'redis://localhost:6379' });
    const subClient = pubClient.duplicate();
    await pubClient.connect();
    await subClient.connect();
    (this.server as any).adapter(createAdapter(pubClient, subClient));
    this.logger.log('ChatGateway initialized with Redis adapter');
  }

  handleConnection(client: Socket) {
    this.logger.log(`Client connected: ${client.id}`);
    client.emit('welcome', { message: 'Welcome to the chat!' });
  }

  handleDisconnect(client: Socket) {
    this.logger.log(`Client disconnected: ${client.id}`);
  }

  // Example message handler
  async handleMessage(client: Socket, payload: any) {
    this.logger.log(`Received message from ${client.id}: ${JSON.stringify(payload)}`);
    this.server.emit('message', payload);
  }
}

