import { Injectable, Logger, NotFoundException, BadRequestException, InternalServerErrorException } from '@nestjs/common';
import { PrismaService } from '../../prisma/prisma.service';
import { Prisma } from '@prisma/client';
import { S3Client, PutObjectCommand, GetObjectCommand } from '@aws-sdk/client-s3';
import { getSignedUrl } from '@aws-sdk/s3-request-presigner';
import { ConfigService } from '@nestjs/config';
import { CreateSnapshotDto, FileMetadataDto } from './dto/create-snapshot.dto';
import { v4 as uuidv4 } from 'uuid';
import { Readable } from 'stream';
import * as crypto from 'crypto';

@Injectable()
export class SnapshotsService {
  private readonly logger = new Logger(SnapshotsService.name);
  private readonly s3Client: S3Client;
  private readonly bucketName: string;
  private readonly presignedUrlExpiration = 3600; // 1 hour

  constructor(
    private prisma: PrismaService,
    private configService: ConfigService,
  ) {
    const awsRegion = this.configService.get<string>('AWS_REGION');
    const awsAccessKeyId = this.configService.get<string>('AWS_ACCESS_KEY_ID');
    const awsSecretAccessKey = this.configService.get<string>('AWS_SECRET_ACCESS_KEY');

    if (!awsRegion || !awsAccessKeyId || !awsSecretAccessKey) {
      throw new Error('AWS configuration is missing. Please set AWS_REGION, AWS_ACCESS_KEY_ID, and AWS_SECRET_ACCESS_KEY environment variables.');
    }

    this.s3Client = new S3Client({
      region: awsRegion,
      credentials: {
        accessKeyId: awsAccessKeyId,
        secretAccessKey: awsSecretAccessKey,
      },
    });

    this.bucketName = this.configService.get<string>('S3_SNAPSHOTS_BUCKET') || 'rhythm-snapshots';
  }

  async createSnapshot(userId: string, dto: CreateSnapshotDto, file?: Express.Multer.File) {
    const { projectId, ...snapshotData } = dto;
    const snapshotId = uuidv4();

    // Verify project exists
    const project = await this.prisma.project.findUnique({
      where: { id: projectId },
    });

    if (!project) {
      throw new NotFoundException('Project not found');
    }

    // For now, allow any authenticated user to create snapshots for public projects
    // In a real application, you might want to implement more granular permissions
    if (!project.isPublic) {
      throw new BadRequestException('This project is not public');
    }

    let fileMetadata: FileMetadataDto[] = [];

    // Handle file upload if present
    if (file) {
      const fileKey = `snapshots/${projectId}/${snapshotId}.zip`;
      await this.uploadToS3(file.buffer, fileKey, file.mimetype);

      fileMetadata = [{
        path: 'snapshot.zip',
        hash: crypto.createHash('sha256').update(file.buffer).digest('hex'),
        mimeType: file.mimetype,
        size: file.size,
      }];
    }

    // Create snapshot record
    const snapshot = await this.prisma.snapshot.create({
      data: {
        id: snapshotId,
        name: snapshotData.name,
        description: snapshotData.description,
        data: JSON.stringify({ files: fileMetadata }),
        projectId,
      },
    });

    return snapshot;
  }

  async getProjectSnapshots(projectId: string, userId: string) {
    // Verify project exists and is public
    const project = await this.prisma.project.findUnique({
      where: { id: projectId },
    });

    if (!project) {
      throw new NotFoundException('Project not found');
    }

    // Only allow access to public projects for now
    if (!project.isPublic) {
      throw new BadRequestException('This project is not public');
    }

    // Get snapshots with basic project info
    const snapshots = await this.prisma.snapshot.findMany({
      where: { projectId },
      orderBy: { createdAt: 'desc' },
      include: {
        project: {
          select: {
            id: true,
            name: true,
            description: true,
            isPublic: true,
            createdAt: true,
            updatedAt: true,
          },
        },
      },
    });

    // Generate signed URLs for files
    const snapshotsWithUrls = await Promise.all(
      snapshots.map(async (snapshot: unknown) => {
        const files: Array<{ path: string; downloadUrl: string }> = [];
        try {
          const snapshotData = (snapshot as any).data as { files?: Array<{ path: string }> };
          if (snapshotData?.files?.length) {
            const filePromises = snapshotData.files.map(async (file) => ({
              ...file,
              downloadUrl: await this.generateDownloadUrl(projectId, (snapshot as any).id, file.path),
            }));

            const processedFiles = await Promise.all(filePromises);
            files.push(...processedFiles);
          }
        } catch (error) {
          this.logger.error('Error processing snapshot files', error);
        }

        return {
          ...snapshot,
          files,
        } as any; // Using any to avoid complex type assertions
      }),
    );

    return snapshotsWithUrls;
  }

  async getSnapshotById(projectId: string, snapshotId: string, userId: string) {
    // Verify project exists and is public
    const project = await this.prisma.project.findUnique({
      where: { id: projectId },
    });

    if (!project) {
      throw new NotFoundException('Project not found');
    }

    if (!project.isPublic) {
      throw new BadRequestException('This project is not public');
    }

    // Get snapshot with signed URLs for files
    const snapshot = await this.prisma.snapshot.findUnique({
      where: { id: snapshotId, projectId },
    });

    if (!snapshot) {
      throw new NotFoundException('Snapshot not found');
    }

    // Process files
    let files: Array<{ path: string; downloadUrl: string }> = [];
    try {
      const snapshotData = snapshot.data as { files?: Array<{ path: string }> };
      if (snapshotData?.files?.length) {
        files = await Promise.all(
          snapshotData.files.map(async (file) => ({
            ...file,
            downloadUrl: await this.generateDownloadUrl(projectId, snapshotId, file.path),
          })),
        );
      }
    } catch (error) {
      this.logger.error('Error processing snapshot files', error);
    }

    return {
      ...snapshot,
      files,
    };
  }

  private generateFileHash(buffer: Buffer): string {
    return crypto.createHash('sha256').update(buffer).digest('hex');
  }

  private async generateDownloadUrl(projectId: string, snapshotId: string, filePath: string): Promise<string> {
    try {
      const fileKey = `snapshots/${projectId}/${snapshotId}/${filePath}`;
      const command = new GetObjectCommand({
        Bucket: this.bucketName,
        Key: fileKey,
      });
      return getSignedUrl(this.s3Client, command, { expiresIn: this.presignedUrlExpiration });
    } catch (error) {
      this.logger.error(`Error generating download URL for ${filePath}`, error);
      throw new InternalServerErrorException('Failed to generate download URL');
    }
  }

  private async uploadToS3(buffer: Buffer, key: string, contentType: string): Promise<string> {
    try {
      const command = new PutObjectCommand({
        Bucket: this.bucketName,
        Key: key,
        Body: buffer,
        ContentType: contentType,
      });

      await this.s3Client.send(command);
      return key;
    } catch (error) {
      this.logger.error('Error uploading to S3', error);
      throw new InternalServerErrorException('Failed to upload file');
    }
  }

  private async streamToBuffer(stream: Readable): Promise<Buffer> {
    return new Promise((resolve, reject) => {
      const chunks: Buffer[] = [];
      stream.on('data', (chunk) => chunks.push(chunk));
      stream.on('error', reject);
      stream.on('end', () => resolve(Buffer.concat(chunks)));
    });
  }
}

import { Controller, Put, Param, Body, UseGuards, Get, Query, Delete } from '@nestjs/common';
import { ApiTags, ApiOperation, ApiResponse, ApiBearerAuth, ApiParam, ApiQuery } from '@nestjs/swagger';
import { JwtAuthGuard } from '../../auth/guards/jwt-auth.guard';
import { GetUser } from '../../common/decorators/get-user.decorator';
import type { JwtPayload } from '../../auth/interfaces/jwt-payload.interface';
import { TagsService } from './tags.service';
import { UpdateTagsDto } from './dto/update-tags.dto';
// Local interface for Tag based on Prisma schema
interface Tag {
  id: string;
  name: string;
  description: string | null;
  color: string | null;
  createdAt: Date;
  updatedAt: Date;
  entityTags: Array<{
    id: string;
    entityType: string;
    entityId: string;
    tagId: string;
    snapshotId: string | null;
    createdAt: Date;
  }>;
}

@ApiTags('tags')
@Controller('api/tags')
@UseGuards(JwtAuthGuard)
@ApiBearerAuth()
export class TagsController {
  constructor(private readonly tagsService: TagsService) {}

  @Put(':entityType/:entityId')
  @ApiOperation({ summary: 'Update tags for an entity' })
  @ApiParam({ name: 'entityType', description: 'Type of the entity (e.g., project, file, snapshot)' })
  @ApiParam({ name: 'entityId', description: 'ID of the entity' })
  @ApiResponse({ status: 200, description: 'Tags updated successfully' })
  @ApiResponse({ status: 400, description: 'Invalid input' })
  @ApiResponse({ status: 401, description: 'Unauthorized' })
  async updateEntityTags(
    @Param('entityType') entityType: string,
    @Param('entityId') entityId: string,
    @GetUser() user: JwtPayload,
    @Body() updateTagsDto: UpdateTagsDto,
  ) {
    return this.tagsService.updateEntityTags(
      entityType,
      entityId,
      user.sub,
      updateTagsDto,
    );
  }

  @Get(':entityType/:entityId')
  @ApiOperation({ summary: 'Get tags for an entity' })
  @ApiParam({ name: 'entityType', description: 'Type of the entity' })
  @ApiParam({ name: 'entityId', description: 'ID of the entity' })
  @ApiResponse({ status: 200, description: 'Returns the tags for the entity' })
  @ApiResponse({ status: 404, description: 'Entity not found' })
  getEntityTags(
    @Param('entityType') entityType: string,
    @Param('entityId') entityId: string,
  ) {
    return this.tagsService.getEntityTags(entityType, entityId);
  }

  @Get()
  @ApiOperation({ summary: 'Get all tags with counts' })
  @ApiQuery({ name: 'search', required: false, description: 'Search term for tag names' })
  @ApiQuery({ name: 'limit', required: false, description: 'Limit the number of results' })
  @ApiResponse({ status: 200, description: 'Returns all tags with their usage counts' })
  @ApiResponse({ status: 401, description: 'Unauthorized' })
  async findAll(
    @Query('search') search?: string,
    @Query('limit') limit?: number
  ) {
    return this.tagsService.findAll({
      search,
      limit: limit ? Number(limit) : undefined,
    });
  }

  @Delete(':tagId')
  @ApiOperation({ summary: 'Delete a tag' })
  @ApiParam({ name: 'tagId', description: 'ID of the tag to delete' })
  @ApiResponse({ status: 200, description: 'Tag deleted successfully' })
  @ApiResponse({ status: 400, description: 'Cannot delete tag in use' })
  @ApiResponse({ status: 404, description: 'Tag not found' })
  deleteTag(
    @Param('tagId') tagId: string,
    @Query('force') force: boolean = false,
  ) {
    return this.tagsService.deleteTag(tagId, force);
  }
}

import { Injectable, NotFoundException, BadRequestException } from '@nestjs/common';
import { PrismaService } from '../../prisma/prisma.service';
import { UpdateTagsDto } from './dto/update-tags.dto';
import { Prisma } from '@prisma/client';

@Injectable()
export class TagsService {
  constructor(private prisma: PrismaService) {}

  /**
   * Update tags for an entity
   * @param entityType Type of the entity (e.g., 'project', 'file', 'snapshot')
   * @param entityId ID of the entity
   * @param userId ID of the user performing the update
   * @param dto UpdateTagsDto containing tag names and optional default color
   * @returns The updated tags for the entity
   */
  async updateEntityTags(
    entityType: string,
    entityId: string,
    userId: string,
    dto: UpdateTagsDto,
  ) {
    const { tags: tagNames, defaultColor = '#6B7280' } = dto;

    // Normalize tag names (trim, lowercase, etc.)
    const normalizedTags = tagNames
      .map((name) => name.trim().toLowerCase())
      .filter((name) => name.length > 0);

    // Get existing tags for this entity
    const existingTags = await this.prisma.entityTag.findMany({
      where: {
        entityType,
        entityId,
      },
      include: {
        tag: {
          include: {
            _count: {
              select: { entityTags: true }
            }
          }
        },
      },
    });

    const existingTagNames = new Set(existingTags.map((et: unknown) => (et as { tag: { name: string } }).tag.name));
    const newTagNames = normalizedTags.filter((name) => !existingTagNames.has(name));

    // Remove tags that are no longer present
    const tagsToRemove = existingTags.filter((et: unknown) => !normalizedTags.includes((et as { tag: { name: string } }).tag.name));

    // Create any new tags that don't exist
    const newTags = await Promise.all(
      newTagNames.map((name) =>
        this.prisma.tag.upsert({
          where: { name },
          create: {
            name,
            color: defaultColor,
          },
          update: {},
        }),
      ),
    );

    // Use a transaction to ensure data consistency
    await this.prisma.$transaction(async (prisma: unknown) => {
      // Remove old tags that are not in the new list
      if (tagsToRemove.length > 0) {
        await prisma.entityTag.deleteMany({
          where: {
            entityType,
            entityId,
            tagId: { in: tagsToRemove.map((t: unknown) => (t as { id: string }).id) },
          },
        });
      }

      // Add new tags
      if (newTags.length > 0) {
        // First, find existing entity tags to avoid duplicates
        const existingEntityTags = await prisma.entityTag.findMany({
          where: {
            entityType,
            entityId,
            tagId: { in: newTags.map((tag) => tag.id) },
          },
          select: { tagId: true },
        });

        const existingTagIds = new Set(existingEntityTags.map((et: unknown) => (et as { tagId: string }).tagId));
        const tagsToCreate = newTags.filter((tag) => !existingTagIds.has(tag.id));

        if (tagsToCreate.length > 0) {
          await prisma.entityTag.createMany({
            data: tagsToCreate.map((tag) => ({
              entityType,
              entityId,
              tagId: tag.id,
              createdById: userId,
            })),
          });
        }
      }
    });

    // Return all tags for the entity after update
    const result = await Promise.all(
      existingTags.map(async (et: unknown) => {
        const tag = await this.prisma.tag.findUnique({
          where: { id: (et as { tagId: string }).tagId },
        });

        if (!tag) {
          throw new NotFoundException(`Tag with ID ${(et as { tagId: string }).tagId} not found`);
        }

        return {
          id: tag.id,
          name: tag.name,
          color: tag.color,
          createdAt: (et as { createdAt: Date }).createdAt,
        };
      })
    );

    const newTagResults = await Promise.all(
      newTags.map(async (tag) => {
        const entityTag = await this.prisma.entityTag.create({
          data: {
            entityType,
            entityId,
            tagId: tag.id,
          },
          include: {
            tag: true,
          },
        });

        return {
          id: entityTag.tag.id,
          name: entityTag.tag.name,
          color: entityTag.tag.color,
          createdAt: entityTag.createdAt,
        };
      }),
    );

    return [...result, ...newTagResults];
  }

  /**
   * Get all tags for an entity
   * @param entityType Type of the entity
   * @param entityId ID of the entity
   * @returns Array of tags with their details
   */
  async getEntityTags(entityType: string, entityId: string) {
    const entityTags = await this.prisma.entityTag.findMany({
      where: {
        entityType,
        entityId,
      },
      include: {
        tag: true,
      },
    });

    return entityTags.map((et: unknown) => ({
      id: (et as { tag: { id: string } }).tag.id,
      name: (et as { tag: { name: string } }).tag.name,
      color: (et as { tag: { color: string } }).tag.color,
      createdAt: (et as { tag: { createdAt: Date } }).tag.createdAt,
    }));
  }

  /**
   * Get all tags with counts
   * @param filter Optional filter by search and limit
   * @returns Array of tags with their usage counts
   */
  async findAll(filter?: { search?: string; limit?: number }) {
    const where: unknown = {}; // Using unknown to avoid Prisma type issues

    if (filter?.search) {
      where.OR = [
        { name: { contains: filter.search } },
        { name: { contains: filter.search.toLowerCase() } },
        { name: { contains: filter.search.toUpperCase() } },
      ];
    }

    const tags = await this.prisma.tag.findMany({
      where,
      include: {
        _count: {
          select: { entityTags: true },
        },
      },
      take: filter?.limit,
    });

    return tags.map((tag: unknown) => ({
      id: (tag as { id: string }).id,
      name: (tag as { name: string }).name,
      description: (tag as { description: string }).description,
      color: (tag as { color: string }).color,
      count: (tag as { _count: { entityTags: number } })._count.entityTags,
    }));
  }

  /**
   * Find entities by tags
   * @param entityType Type of entities to find
   * @param tags Array of tag names to filter by
   * @returns Array of entity IDs that have all the specified tags
   */
  async findEntitiesByTags(entityType: string, tags: string[]): Promise<string[]> {
    if (!tags.length) return [];

    interface QueryResult {
      entityId: string;
    }

    // Convert tags to a comma-separated string with proper escaping
    const tagsList = tags.map(tag => `'${tag.replace(/'/g, "''")}'`).join(',');

    const query = `
      SELECT "entityId"
      FROM "EntityTag" et
      JOIN "Tag" t ON et."tagId" = t.id
      WHERE et."entityType" = $1
      AND t.name IN (${tagsList})
      GROUP BY "entityId"
      HAVING COUNT(DISTINCT t.name) = $2
    `;

    const result = await this.prisma.$queryRawUnsafe(query, entityType, tags.length) as QueryResult[];
    return result.map(r => r.entityId);
  }

  /**
   * Delete a tag by ID
   * @param tagId ID of the tag to delete
   * @param force If true, will delete the tag even if it's in use
   */
  async deleteTag(tagId: string, force = false) {
    const tag = await this.prisma.tag.findUnique({
      where: { id: tagId },
      include: {
        _count: { select: { entityTags: true } },
      },
    });

    if (!tag) {
      throw new NotFoundException(`Tag with ID ${tagId} not found`);
    }

    if (tag._count.entityTags > 0 && !force) {
      throw new BadRequestException(
        `Cannot delete tag '${tag.name}' as it's still in use. Use force=true to delete anyway.`,
      );
    }

    await this.prisma.$transaction([
      this.prisma.entityTag.deleteMany({
        where: { tagId },
      }),
      this.prisma.tag.delete({
        where: { id: tagId },
      }),
    ]);
  }
}

import { 
  WebSocketGateway, 
  WebSocketServer, 
  OnGatewayInit, 
  OnGatewayConnection, 
  OnGatewayDisconnect, 
