export interface TestSocketClient extends Socket {
  testId: string;
  userId?: string;
  projectId?: string;
}

export interface TestSocketOptions {
  auth?: {
    token?: string;
    userId?: string;
  };
  query?: Record<string, string>;
  autoConnect?: boolean;
}

/**
 * Creates a test WebSocket client
 */
export function createTestSocket(
  context: WsTestContext,
  options: TestSocketOptions = {}
): TestSocketClient {
  const { auth = {}, query = {}, autoConnect = true } = options;

  // Build query string with auth if provided
  const queryParams = new URLSearchParams(query);
  if (auth.token) {
    queryParams.set('token', auth.token);
  }
  if (auth.userId) {
    queryParams.set('userId', auth.userId);
  }

  // Create socket client
  const client = io(context.serverUrl, {
    path: '/ws',
    transports: ['websocket'],
    autoConnect,
    forceNew: true,
    reconnection: false,
    query: Object.fromEntries(queryParams),
  }) as TestSocketClient;

  // Add test ID for debugging
  client.testId = `test-${Math.random().toString(36).substring(2, 8)}`;

  // Add user ID if provided
  if (auth.userId) {
    client.userId = auth.userId;
  }

  return client;
}

/**
 * Waits for a socket connection to be established
 */
export function waitForConnection(client: Socket): Promise<void> {
  return new Promise((resolve, reject) => {
    if (client.connected) {
      return resolve();
    }

    const timeout = setTimeout(() => {
      client.off('connect', onConnect);
      client.off('connect_error', onError);
      reject(new Error('Connection timeout'));
    }, 5000);

    const onConnect = () => {
      clearTimeout(timeout);
      client.off('connect_error', onError);
      resolve();
    };

    const onError = (err: Error) => {
      clearTimeout(timeout);
      client.off('connect', onConnect);
      reject(err);
    };

    client.once('connect', onConnect);
    client.once('connect_error', onError);
  });
}

/**
 * Waits for a specific event on a socket
 */
export function waitForEvent<T = any>(
  client: Socket,
  event: string
): Promise<T> {
  return new Promise((resolve, reject) => {
    const timeout = setTimeout(() => {
      client.off(event, onEvent);
      reject(new Error(`Timeout waiting for event: ${event}`));
    }, 5000);

    const onEvent = (data: T) => {
      clearTimeout(timeout);
      resolve(data);
    };

    client.once(event, onEvent);
  });
}

/**
 * Disconnects all test clients
 */
export async function disconnectAllClients(...clients: Socket[]): Promise<void> {
  await Promise.all(
    clients.map(
      (client) =>
        new Promise<void>((resolve) => {
          if (client.connected) {
            client.once('disconnect', () => resolve());
            client.disconnect();
          } else {
            resolve();
          }
        })
    )
  );
}

/**
 * Creates multiple test clients
 */
export function createTestClients(
  context: WsTestContext,
  count: number,
  options: TestSocketOptions = {}
): TestSocketClient[] {
  return Array.from({ length: count }, (_, i) =>
    createTestSocket(context, {
      ...options,
      auth: {
        ...options.auth,
        userId: options.auth?.userId || `test-user-${i + 1}`,
      },
    })
  );
}

import WebSocket, { Server as WebSocketServer } from 'ws';
import { createServer, Server as HttpServer } from 'http';

describe('WebSocket Integration Tests', () => {
  let httpServer: HttpServer;
  let wss: WebSocketServer;
  let port: number;
  let clients: WebSocket[] = [];

  beforeAll((done) => {
    // Create HTTP server
    httpServer = createServer();

    // Create WebSocket server
    wss = new WebSocketServer({ server: httpServer });

    // Handle WebSocket connections
    wss.on('connection', (ws: WebSocket) => {
      const clientId = Math.random().toString(36).substring(2, 9);
      (ws as unknown as { id: string }).id = clientId;

      // Store the WebSocket connection
      clients.push(ws);

      // Handle incoming messages
      ws.on('message', (data: unknown) => {
        try {
          const message = (data as Buffer).toString();

          // Echo the message back to the client
          ws.send(JSON.stringify({ 
            type: 'echo', 
            data: message,
            clientId,
            timestamp: new Date().toISOString()
          }));
        } catch (err) {
          console.error(`[${clientId}] Error handling message:`, err);
        }
      });

      // Handle close events
      ws.on('close', () => {
        clients = clients.filter(c => c !== ws);
      });

      // Handle errors
      ws.on('error', (error: Error) => {
        console.error(`[${clientId}] WebSocket error:`, error);
        clients = clients.filter(c => c !== ws);
      });

      // Send welcome message
      ws.send(JSON.stringify({
        type: 'connected',
        clientId,
        timestamp: new Date().toISOString()
      }));
    });

    // Start server
    httpServer.listen(0, () => {
      port = (httpServer.address() as any).port;
      done();
    });
  });

  afterAll((done) => {
    // Close all client connections
    clients.forEach(client => {
      try {
        client.close(1000, 'Test complete');
      } catch (err) {
        console.error('Error closing client:', err);
      }
    });

    // Close WebSocket server
    wss.close(() => {
      // Close HTTP server
      httpServer.close(() => {
        clients = [];
        console.log('Test teardown complete');
        done();
      });
    });
  });

  it('should handle basic WebSocket communication', (done) => {
    const ws = new WebSocket(`ws://localhost:${port}`);

    ws.on('open', () => {
      ws.send(JSON.stringify({ type: 'test', message: 'Hello' }));
    });

    ws.on('message', (data: unknown) => {
      const message = JSON.parse((data as Buffer).toString());
      expect(message.type).toBe('echo');
      expect(message.data).toContain('Hello');
      ws.close();
      done();
    });

    ws.on('error', (error: Error) => {
      console.error('WebSocket error:', error);
      done(error);
    });
  });

  it('should handle multiple clients', (done) => {
    const ws1 = new WebSocket(`ws://localhost:${port}`);
    const ws2 = new WebSocket(`ws://localhost:${port}`);
    let messagesReceived = 0;

    const checkComplete = () => {
      messagesReceived++;
      if (messagesReceived >= 2) {
        ws1.close();
        ws2.close();
        done();
      }
    };

    ws1.on('open', () => {
      ws1.send(JSON.stringify({ type: 'test', message: 'Client 1' }));
    });

    ws2.on('open', () => {
      ws2.send(JSON.stringify({ type: 'test', message: 'Client 2' }));
    });

    ws1.on('message', (data: unknown) => {
      const message = JSON.parse((data as Buffer).toString());
      expect(message.type).toBe('echo');
      checkComplete();
    });

    ws2.on('message', (data: unknown) => {
      const message = JSON.parse((data as Buffer).toString());
      expect(message.type).toBe('echo');
      checkComplete();
    });

    ws1.on('error', (error: Error) => {
      console.error('Client 1 error:', error);
      done(error);
    });

    ws2.on('error', (error: Error) => {
      console.error('Client 2 error:', error);
      done(error);
    });
  });
});

