process.on('message', (message: unknown) => {
  if (typeof message === 'object' && message !== null && 'type' in message) {
    const msg = message as ShutdownMessage;
    if (msg.type === 'shutdown') {
      shutdown();
    }
  }
});

// Handle uncaught exceptions
