import { Test, TestingModule } from '@nestjs/testing';
import { INestApplication, Logger } from '@nestjs/common';
import { IoAdapter } from '@nestjs/platform-socket.io';
import { ExpressAdapter } from '@nestjs/platform-express';
import express from 'express';
import { createServer, Server as HttpServer } from 'http';
import { AppModule } from '../src/app.module';

const logger = new Logger('WsTestSetup');

export interface WsTestContext {
  app: INestApplication;
  httpServer: HttpServer;
  module: TestingModule;
  serverUrl: string;
}

/**
 * Creates a test application with WebSocket support
 */
export async function createWsTestApp(): Promise<WsTestContext> {
  const expressApp = express();
  const httpServer = createServer(expressApp);

  // Create testing module with required dependencies
  const moduleRef = await Test.createTestingModule({
    imports: [AppModule],
  }).compile();

  // Create Nest application with Express adapter
  const app = moduleRef.createNestApplication(
    new ExpressAdapter(expressApp as any),
    { logger: ['error', 'warn', 'log'] }
  ) as INestApplication;

  // Configure WebSocket adapter
  const ioAdapter = new IoAdapter(httpServer);
  app.useWebSocketAdapter(ioAdapter);

  try {
    // Initialize the application
    await app.init();

    // Start the HTTP server on a random port
    await new Promise<void>((resolve) => {
      httpServer.listen(0, () => {
        const address = httpServer.address();
        if (address && typeof address === 'object') {
          logger.log(`Test server listening on port ${address.port}`);
        }
        resolve();
      });
    });

    // Get the server URL
    const address = httpServer.address();
    let port: number;

    if (address === null) {
      port = 0;
    } else if (typeof address === 'string') {
      port = parseInt(address.split(':').pop() || '0', 10);
    } else {
      port = address.port || 0;
    }

    return { 
      app, 
      httpServer, 
      module: moduleRef,
      serverUrl: `http://localhost:${port}`
    };
  } catch (error) {
    logger.error('Failed to create test app', error);
    await teardownTestApp(app, httpServer);
    throw error;
  }
}

/**
 * Tears down the test application and releases resources
 */
export async function teardownTestApp(
  app: INestApplication | null | undefined, 
  httpServer: HttpServer | null | undefined
): Promise<void> {
  try {
    // Close the HTTP server if it exists
    if (httpServer) {
      await new Promise<void>((resolve) => {
        if (httpServer.listening) {
          httpServer.close(() => {
            logger.log('Test HTTP server closed');
            resolve();
          });
        } else {
          resolve();
        }
      });
    }

    // Close the Nest application if it exists
    if (app) {
      await app.close();
      logger.log('Nest application closed');
    }
  } catch (error) {
    logger.error('Error during test teardown', error);
    throw error;
  }
}

import { createServer, Server as HttpServer } from 'http';
import { Server } from 'socket.io';
import { AddressInfo } from 'net';
import { io, Socket } from 'socket.io-client';

describe('Basic WebSocket Test', () => {
  let httpServer: HttpServer;
  let ioServer: any;
  let socket: Socket;
  let port: number;

  beforeAll((done) => {
    console.log('Setting up test environment...');

    // Create HTTP server
    httpServer = createServer();

    // Create Socket.IO server
    ioServer = new Server(httpServer, {
      cors: {
        origin: '*',
        methods: ['GET', 'POST']
      },
      transports: ['websocket', 'polling'],
      pingInterval: 10000,
      pingTimeout: 5000
    });

    // Handle connections
    ioServer.on('connection', (socket) => {
      console.log(`Client connected: ${socket.id}`);

      socket.on('message', (data) => {
        console.log(`Received message from ${socket.id}:`, data);
        socket.emit('message', { echo: data, timestamp: Date.now() });
      });

      socket.on('ping', () => {
        console.log(`Ping from ${socket.id}`);
        socket.emit('pong', { timestamp: Date.now() });
      });

      socket.on('disconnect', () => {
        console.log(`Client disconnected: ${socket.id}`);
      });
    });

    // Start server
    httpServer.listen(0, '127.0.0.1', () => {
      port = (httpServer.address() as AddressInfo).port;
      console.log(`Test server listening on port ${port}`);
      done();
    });
  });

  afterAll((done) => {
    console.log('Cleaning up...');

    // Helper function to close everything
    const closeAll = () => {
      if (socket && socket.connected) {
        console.log('Disconnecting client...');
        socket.disconnect();
      }

      if (ioServer) {
        console.log('Closing Socket.IO server...');
        ioServer.close();
      }

      if (httpServer) {
        console.log('Closing HTTP server...');
        httpServer.close((err) => {
          if (err) {
            console.error('Error closing HTTP server:', err);
          } else {
            console.log('Test server closed');
          }
          done();
