import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { SettingsAccount } from "@/components/ui";
import { useToast } from "@/components/ui/use-toast";
import useAuth, { User } from "@/hooks/useAuth";

interface UserData extends Partial<User> {
  displayName: string;
  email: string;
  bio: string;
  avatar?: string;
}

const AccountSettings = () => {
  const { toast } = useToast();
  const { auth } = useAuth();
  const navigate = useNavigate();
  const [userData, setUserData] = useState<UserData>({
    displayName: "",
    email: "",
    bio: "",
    avatar: "",
  });

  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Only update state if we have auth data
    if (auth?.user) {
      setUserData({
        displayName:
          auth.user.displayName || auth.user.email?.split("@")[0] || "User",
        email: auth.user.email || "",
        bio: auth.user.bio || "",
        avatar: auth.user.avatar || "",
      });
      setIsLoading(false);
    } else if (auth === null) {
      // Only redirect if we're certain there's no auth (not just loading)
      navigate("/signin");
    }
  }, [auth, navigate]);

  const handleUpdateAccount = async (data: {
    displayName: string;
    email: string;
    bio: string;
  }) => {
    try {
      // In a real app, you would call your API here
      // await updateUserProfile(data);

      // For demo, update local state
      const updatedUser = {
        ...auth?.user,
        ...data,
      };

      // Update localStorage
      const users = JSON.parse(localStorage.getItem("users") || "[]");
      // TODO: Replace 'unknown' with a specific User type if available
      const updatedUsers = users.map((user: unknown) =>
        user.id === updatedUser.id ? updatedUser : user,
      );
      localStorage.setItem("users", JSON.stringify(updatedUsers));

      // Update auth state
      localStorage.setItem(
        "auth",
        JSON.stringify({
          ...auth,
          user: updatedUser,
        }),
      );

      setUserData((prev) => ({
        ...prev,
        ...data,
      }));

      toast({
        title: "Account Updated",
        description: "Your account information has been successfully updated.",
      });
    } catch (error) {
      console.error("Failed to update account:", error);
      toast({
        title: "Update Failed",
        description:
          "There was an error updating your account. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleRescanPlugins = () => {
    // In a real app, you would call your API here
    // await rescanPlugins();

    // Simulate plugin scanning
    setTimeout(() => {
      toast({
        title: "Plugin Scan Complete",
        description: "Your plugin library has been successfully rescanned.",
      });
    }, 1500);
  };

  const handleAvatarChange = async (file: File) => {
    try {
      // In a real app, you would upload the file to your server
      // const avatarUrl = await uploadAvatar(file);
      const avatarUrl = URL.createObjectURL(file); // Temporary URL for demo

      if (!auth?.user) {
        throw new Error("User not authenticated");
      }

      // Update localStorage
      const users = JSON.parse(localStorage.getItem("users") || "[]");
      const updatedUser = {
        ...auth.user,
        avatar: avatarUrl,
      };

      // TODO: Replace 'unknown' with a specific User type if available
      const updatedUsers = users.map((user: unknown) =>
        user.id === updatedUser.id ? updatedUser : user,
      );

      localStorage.setItem("users", JSON.stringify(updatedUsers));

      // Update auth state
      localStorage.setItem(
        "auth",
        JSON.stringify({
          ...auth,
          user: updatedUser,
        }),
      );

      setUserData((prev) => ({
        ...prev,
        avatar: avatarUrl,
      }));

      toast({
        title: "Avatar Updated",
        description: "Your profile picture has been updated.",
      });
    } catch (error) {
      console.error("Failed to update avatar:", error);
      toast({
        title: "Upload Failed",
        description:
          "There was an error updating your avatar. Please try again.",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="space-y-6">
      <SettingsAccount
        initialData={{
          displayName: userData.displayName,
          email: userData.email,
          bio: userData.bio || "",
          avatar: userData.avatar,
        }}
        onUpdateAccount={handleUpdateAccount}
        onRescanPlugins={handleRescanPlugins}
        onAvatarChange={handleAvatarChange}
      />
    </div>
  );
};

export default AccountSettings;

import * as React from "react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Settings, User, Lock, Bell, CreditCard } from "lucide-react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

const settingsTabs = [
  { id: "account", label: "Account", icon: <User className="h-4 w-4" /> },
  { id: "security", label: "Security", icon: <Lock className="h-4 w-4" /> },
  {
    id: "notifications",
    label: "Notifications",
    icon: <Bell className="h-4 w-4" />,
  },
  { id: "billing", label: "Billing", icon: <CreditCard className="h-4 w-4" /> },
];

interface SettingsLayoutProps {
  children: React.ReactNode;
}

export function SettingsLayout({ children }: SettingsLayoutProps) {
  const location = useLocation();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState("account");

  useEffect(() => {
    const path = location.pathname.split("/").pop() || "account";
    setActiveTab(path === "settings" ? "account" : path);
  }, [location.pathname]);

  return (
    <div className="container mx-auto py-8 max-w-6xl">
      <div className="flex items-center space-x-4 mb-8">
        <Settings className="h-8 w-8 text-purple-400" />
        <h1 className="text-3xl font-bold">Settings</h1>
      </div>

      <Tabs value={activeTab} className="space-y-8">
        <TabsList className="w-full justify-start bg-gray-900 p-1 h-auto rounded-lg">
          {settingsTabs.map((tab) => {
            const to =
              tab.id === "account" ? "/settings" : `/settings/${tab.id}`;
            return (
              <Link to={to} key={tab.id} className="w-full">
                <TabsTrigger
                  value={tab.id}
                  className="w-full justify-start space-x-2 data-[state=active]:bg-gray-800 data-[state=active]:text-white"
                >
                  {tab.icon}
                  <span>{tab.label}</span>
                </TabsTrigger>
              </Link>
            );
          })}
        </TabsList>

        <TabsContent value={activeTab} className="mt-0">
          <div className="bg-gray-900 rounded-lg p-6">{children}</div>
        </TabsContent>
      </Tabs>
    </div>
  );
}
